site.headStyle:
.button {
  display: inline-block;
  padding: 10px 20px;
  background-color: #007bff;
  color: #ffffff;
  text-decoration: none;
  transition: background-color 0.3s ease;
}

.button:hover {
  background-color: #0056b3; /* slightly darker color */
}

@media (min-width:481px) {
  body {
    overflow-x: hidden;
  }

  .silex-runtime .animation-element.slide-left {
    opacity: 0;
    -moz-transition: all 500ms ease-out;
    -webkit-transition: all 500ms ease-out;
    -o-transition: all 500ms ease-out;
    transition: all 500ms ease-out;
    -moz-transform: translate3d(-100px, 0px, 0px);
    -webkit-transform: translate3d(-100px, 0px, 0px);
    -o-transform: translate(-100px, 0px);
    -ms-transform: translate(-100px, 0px);
    transform: translate3d(-100px, 0px, 0px);
  }

  .silex-runtime .animation-element.slide-right {
    opacity: 0;
    -moz-transition: all 500ms ease-out;
    -webkit-transition: all 500ms ease-out;
    -o-transition: all 500ms ease-out;
    transition: all 500ms ease-out;
    -moz-transform: translate3d(100px, 0px, 0px);
    -webkit-transform: translate3d(100px, 0px, 0px);
    -o-transform: translate(100px, 0px);
    -ms-transform: translate(100px, 0px);
    transform: translate3d(100px, 0px, 0px);
  }

  .silex-runtime .animation-element.slide-fade {
    opacity: 0;
    -moz-transition: all 800ms ease-out;
    -webkit-transition: all 800ms ease-out;
    -o-transition: all 800ms ease-out;
    transition: all 800ms ease-out;
  }

  .silex-runtime .animation-element.slide-top1,
  .silex-runtime .animation-element.slide-top2,
  .silex-runtime .animation-element.slide-top3 {
    opacity: 0;
    -moz-transition: all 800ms ease-out;
    -webkit-transition: all 800ms ease-out;
    -o-transition: all 800ms ease-out;
    transition: all 800ms ease-out;
    -moz-transform: translate3d(0px, 100px, 0px);
    -webkit-transform: translate3d(0px, 100px, 0px);
    -o-transform: translate(0px, 100px);
    -ms-transform: translate(0px, 100px);
    transform: translate3d(0px, 100px, 0px);
  }

  .silex-runtime .animation-element.slide-top2 {
    -moz-transform: translate3d(0px, 200px, 0px);
    -webkit-transform: translate3d(0px, 200px, 0px);
    -o-transform: translate(0px, 200px);
    -ms-transform: translate(0px, 200px);
    transform: translate3d(0px, 200px, 0px);
  }

  .silex-runtime .animation-element.slide-top3 {
    -moz-transform: translate3d(0px, 300px, 0px);
    -webkit-transform: translate3d(0px, 300px, 0px);
    -o-transform: translate(0px, 300px);
    -ms-transform: translate(0px, 300px);
    transform: translate3d(0px, 300px, 0px);
  }

  .silex-runtime .animation-element.in-view {
    opacity: 1;
    -moz-transform: translate3d(0px, 0px, 0px);
    -webkit-transform: translate3d(0px, 0px, 0px);
    -o-transform: translate(0px, 0px);
    -ms-transform: translate(0px, 0px);
    transform: translate3d(0px, 0px, 0px);
  }
}
.navbar {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  background-color: #333;
  padding: 20px 0;
  transition: padding 0.3s ease;
  z-index: 1000;
}

.navbar.shrink {
  padding: 10px 0;
}

.navbar .logo {
  color: #fff;
  font-size: 24px;
  margin-left: 20px;
  text-decoration: none;
}

.navbar .nav-links {
  list-style: none;
  margin: 0;
  padding: 0;
  text-align: right;
}

.navbar .nav-links li {
  display: inline-block;
  margin-right: 20px;
}

.navbar .nav-links li a {
  color: #fff;
  text-decoration: none;
  transition: color 0.3s ease;
}

.navbar .nav-links li a:hover {
  color: #ff0; /* Change color on hover */
}

